package web3rpc.client

import io.kotest.core.spec.style.BehaviorSpec
import io.kotest.matchers.shouldBe
import org.openapitools.codegen.CodegenConfig
import java.util.*

class KlaytnTypescriptAxiosClientCodeGenTest : BehaviorSpec({
    Given("Klaytn typescript axios code generator") {
        When("Load by ServiceLoader") {
            Then("should be existed") {
                val loader = ServiceLoader.load(
                    CodegenConfig::class.java,
                    CodegenConfig::class.java.classLoader
                )

                for (config in loader) {
                    println(config.name)
                }
                loader.find { i -> i.name == KlaytnTypescriptAxiosClientCodegen.clientName}?.name.shouldBe(KlaytnTypescriptAxiosClientCodegen.clientName)
            }
        }
        When("Model file name set to KlaytnModel") {
            Then("return 'klaytn-model as kebab case") {
                val codeGen = KlaytnTypescriptAxiosClientCodegen()
                codeGen.toModelFilename("KlaytnModel").shouldBe("klaytn-model")
            }
        }
    }
})
